version.script:
import org.efaps.admin.runlevel.RunLevel;
import org.efaps.db.Context;
import org.efaps.db.SearchQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;

import java.net.URL
import java.io.File;

EFAPS_LOGGER.info("Importing Data for eFapsApp-Contacts");

RunLevel.init("shell");
RunLevel.execute();

SearchQuery query = new SearchQuery();
query.setQueryTypes("Admin_User_JAASKey");
query.addWhereExprEqValue("Key", "Contacts_Admin");
query.addSelect("OID");
query.execute();
if (!query.next()) {
    URL url1 = new URL(EFAPS_ROOTURL, "Data/IMPORT_AttributeDefinitions.xml");
    DataImport imp1 = DataImport.readFile(url1);
    imp1.updateInDB();

    URL url2 = new URL(EFAPS_ROOTURL, "Data/IMPORT_UserJAASKey.xml");
    DataImport imp2 = DataImport.readFile(url2);
    imp2.updateInDB();
}


UIUpdate update = new UIUpdate();
update.add2Menu("d7ede363-6f81-4576-b2a3-c02f1d8c749b", "87001cc3-c45c-44de-b8f1-776df507f268", 1);


update.add2Help("0727550d-4d8a-4aec-b5f4-b2e9290184e3", "dead549e-5cc6-49f9-9a79-8e33aa139f6d", -1);
